import React from 'react';

import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';

import { makeStyles } from '@material-ui/core/styles';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

import axios from 'axios';

const PesquisarFuncionario = () => {
    if(document.querySelector("[name='txtNomeFuncionario']").value == ""){
        alert("Nome deve ser preenchido");
    }

}

const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
    },
    margin: {
      margin: theme.spacing(1),
    },
    withoutLabel: {
      marginTop: theme.spacing(3),
    },
    textField: {
      width: '25ch',
    },
    table:{
        // minwidth:1000,
        maxWidth:500,
        "& .MuiTableCell-root": {
            borderLeft: "1px solid rgba(0, 0, 0, 1)",
            borderRight: "1px solid rgba(0, 0, 0, 1)",
            borderBottom: "1px solid rgba(0, 0, 0, 1)",
        },
    },
    tableRow: {
        "&:last-child th, &:last-child td": {
          borderBottom: 0
        }
    }
}));

function createData(id, nomeAssociado, area, telefone) {
    return { id, nomeAssociado, area, telefone };
}

const rows = [
    createData(1,'Felipe','11','973400876'),
    createData(2,'Luis','11','993354686'),
    createData(3,'Guilherme','12','997254786'),
    createData(4,'Leonardo','13','988150807'),
    createData(5,'Carlos','13','996335449'),
]

//const classes = useStyles();

export default class PesquisarDados extends React.Component{
    state = {
        lista: []
    }

    componentDidMount(){
        axios.get('http://localhost:8080/associates/')
            .then(res => {
                const lista = res.data;
                //console.log(lista);
                this.setState({lista});
            })
    }

    render(){
        // component={Paper}
        return(
            <TableContainer style={{marginLeft:"5px",marginRight:"5px"}}>
                <Table style={{borderColor:'black', borderStyle:'solid'}}>
                    <TableHead style={{fontWeight:"bold"}}>
                    {/* style={{backgroundColor:'blue'}} */}
                    {/* style={{color:'white', fontWeight:"bold"}} */}
                        <TableRow>
                            <TableCell 
                                // width="75" 
                                style={{fontWeight:"bold"}} 
                                align="center"
                            >
                                Id
                            </TableCell>
                            <TableCell 
                                // width="635" 
                                style={{fontWeight:"bold", borderLeftStyle:"double", borderLeftColor:'black'}} 
                                align="center"
                            >
                                Nome do Associado
                            </TableCell>
                            <TableCell 
                                // width="40" 
                                style={{fontWeight:"bold"}} 
                                align="center"
                            >
                                √Årea
                            </TableCell>
                            <TableCell 
                                // width="150" 
                                style={{fontWeight:"bold"}} 
                                align="center"
                            >
                                Telefone
                            </TableCell>
                            <TableCell 
                                // width="75" 
                                style={{fontWeight:"bold"}} 
                                align="center"
                            >
                                Editar Associado
                            </TableCell>
                            <TableCell 
                                // width="75" 
                                style={{fontWeight:"bold"}} 
                                align="center"
                            >
                                Editar Medidas
                            </TableCell>
                            <TableCell 
                                // width="75" 
                                style={{fontWeight:"bold"}} 
                                align="center"
                            >
                                Editar Mapeamento
                            </TableCell>
                            <TableCell 
                                // width="75" 
                                style={{fontWeight:"bold"}} 
                                align="center"
                            >
                                Excluir Associado
                            </TableCell>
                        </TableRow>
                    </TableHead>
                    {/* style={{borderRightWidth:1, borderColor:'red', borderStyle:'solid'}} */}
                    <TableBody>
                        {this.state.lista.map((row) => (
                            <TableRow key={row.id}>
                                <TableCell 
                                    // style={{
                                    //     borderRightWidth:1, 
                                    //     borderColor:'black', 
                                    //     borderStyle:'solid'
                                    // }} 
                                    // width="75" 
                                    component="th" 
                                    scope="row" 
                                >
                                    {row.id}
                                </TableCell>
                                <TableCell 
                                    // width="635"
                                    style={{
                                        borderColor:'black', 
                                        borderBottomStyle:'ridge'
                                    }}
                                >
                                    {row.name}
                                </TableCell>
                                <TableCell 
                                    // width="40" 
                                    align="center"
                                    // style={{
                                    //     borderRightWidth:1, 
                                    //     borderColor:'black', 
                                    //     borderStyle:'solid'
                                    // }}
                                >
                                    {row.area}
                                </TableCell>
                                <TableCell 
                                    // width="150"
                                    // style={{
                                    //     borderRightWidth:1, 
                                    //     borderColor:'black', 
                                    //     borderStyle:'solid'
                                    // }}
                                >
                                    {row.phone}
                                </TableCell>
                                <TableCell 
                                    // width="75" 
                                    align="center"
                                    // style={{
                                    //     borderRightWidth:1, 
                                    //     borderColor:'black', 
                                    //     borderStyle:'solid'
                                    // }}
                                >
                                    <EditIcon />
                                </TableCell>
                                <TableCell 
                                    // width="75" 
                                    align="center"
                                    // style={{
                                    //     borderRightWidth:1, 
                                    //     borderColor:'black', 
                                    //     borderStyle:'solid'
                                    // }}
                                >
                                    <EditIcon />
                                </TableCell>
                                <TableCell 
                                    // width="75" 
                                    align="center"
                                    // style={{
                                    //     borderRightWidth:1, 
                                    //     borderColor:'black', 
                                    //     borderStyle:'solid'
                                    // }}
                                >
                                    <EditIcon />
                                </TableCell>
                                <TableCell 
                                    // width="75"
                                    align="center"
                                    // style={{
                                    //     borderRightWidth:1, 
                                    //     borderColor:'black', 
                                    //     borderStyle:'solid'
                                    // }}
                                >
                                    <DeleteIcon />
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        )
    }
}
