import React from 'react';

import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import MedidasIcon from '@material-ui/icons/Assignment';
import MapeamentoIcon from '@material-ui/icons/LocalHospital';

import Fab from '@material-ui/core/Fab';
import Tooltip from '@material-ui/core/Tooltip';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

import axios from 'axios';

function ObterNomeLink(caminhoLink, valor){
    return caminhoLink + "?id=" + valor;
}

export default class PesquisarDados extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            lista: []
        }
    }

    componentDidMount(){
        axios.get('http://localhost:8080/associates/')
            .then(res => {
                const lista = res.data;
                this.setState({lista});
            });
    }

    handleDeleteAssociado = (itemId) => {
        axios.delete("http://localhost:8080/associates/" + itemId,
            {params:{id:itemId}}).then(response => {
                alert("Associado excluído com sucesso!");
            })
    }

    render(){
        return(
            <div>
                <TableContainer style={{marginLeft:"5px",marginRight:"5px"}}>
                    <Table style={{borderColor:'black', borderStyle:'solid', width:800}}>
                        <TableHead style={{fontWeight:"bold", borderBottomStyle:"solid"}}>
                            <TableRow>
                                <TableCell 
                                    width="50" 
                                    style={{fontWeight:"bold"}}
                                    align="center"
                                >
                                    Id
                                </TableCell>
                                <TableCell 
                                    width="350" 
                                    style={{fontWeight:"bold", borderLeftStyle:"double", borderLeftColor:'black'}}
                                    align="center"
                                >
                                    Nome do Associado
                                </TableCell>
                                <TableCell 
                                    width="200"
                                    style={{fontWeight:"bold", borderLeftStyle:"double", borderLeftColor:'black'}}
                                    align="center"
                                >
                                    Associado
                                </TableCell>
                                <TableCell 
                                    width="100"
                                    style={{fontWeight:"bold", borderLeftStyle:"double", borderLeftColor:'black'}}
                                    align="center"
                                >
                                    Medidas
                                </TableCell>
                                <TableCell 
                                    width="100"
                                    style={{fontWeight:"bold", borderLeftStyle:"double", borderLeftColor:'black'}}
                                    align="center"
                                >
                                    Mapeamento
                                </TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {this.state.lista.map((row) => (
                                <TableRow key={row.id}>
                                    <TableCell 
                                        style={{
                                            borderColor:'black', 
                                            borderBottomStyle:'ridge',
                                            borderRightStyle:"double", 
                                            borderRightColor:'black'
                                        }} 
                                        width="50" 
                                        component="th" 
                                        scope="row" 
                                    >
                                        {row.id}
                                    </TableCell>
                                    <TableCell 
                                        width="350"
                                        style={{
                                            borderColor:'black', 
                                            borderBottomStyle:'ridge',
                                            borderRightStyle:"double", 
                                            borderRightColor:'black'
                                        }}
                                    >
                                        {row.name}
                                    </TableCell>
                                    <TableCell 
                                        width="200"
                                        align="center"
                                        style={{
                                            borderColor:'black', 
                                            borderBottomStyle:'ridge',
                                            borderRightStyle:"double", 
                                            borderRightColor:'black'
                                        }}
                                    >
                                        <Tooltip title="Editar Associado">
                                            <Fab 
                                                color="default" 
                                                style={{marginRight:"30px"}}
                                                href={ObterNomeLink("dadosfuncionarios",row.id)}
                                            >
                                                <EditIcon />
                                            </Fab>
                                        </Tooltip>
                                        <Tooltip title="Excluir Associado">
                                            <Fab 
                                                color="secondary"
                                                onClick={() => this.handleDeleteAssociado(row.id)}
                                            >
                                                <DeleteIcon />
                                            </Fab>
                                        </Tooltip>
                                    </TableCell>
                                    <TableCell 
                                        width="100"
                                        align="center"
                                        style={{
                                            borderColor:'black', 
                                            borderBottomStyle:'ridge',
                                            borderRightStyle:"double", 
                                            borderRightColor:'black'
                                        }}
                                    >
                                        <Tooltip title="Controle de Medidas">
                                            <Fab 
                                                color="primary" 
                                                style={{marginRight:"30px"}}
                                                href={ObterNomeLink("dadosmedidas",row.id)}
                                            >
                                                <MedidasIcon />
                                            </Fab>
                                        </Tooltip>
                                    </TableCell>
                                    <TableCell 
                                        width="100"
                                        align="center"
                                        style={{
                                            borderColor:'black', 
                                            borderBottomStyle:'ridge'
                                        }}
                                    >
                                        <Tooltip title="Controle de Mapeamento">
                                            <Fab 
                                                color="primary" 
                                                style={{marginRight:"30px"}}
                                                href={ObterNomeLink("dadosmapeamento",row.id)}
                                            >
                                                <MapeamentoIcon />
                                            </Fab>
                                        </Tooltip>
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            </div>
        )
    }
}
