import React from "react"
import Link from 'next/link'

import { library } from '@fortawesome/fontawesome-svg-core';
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import { faPlusCircle } from "@fortawesome/free-solid-svg-icons";
library.add( faSearch);
library.add( faPlusCircle);
import { FontAwesomeIcon as FaIcon } from "@fortawesome/react-fontawesome";

var nomeFuncionario = "";

const onClick_Search = function(){ 
    nomeFuncionario = document.querySelector("[name='txtNomeFuncionario']").value;
    
    if(nomeFuncionario == ""){
        alert("Nome deve ser preenchido");
    }
    else{
        alert(nomeFuncionario);
    }
}

const Search = () => {
    return <FaIcon onClick={onClick_Search} icon="search" />
};

const PlusCircle = () => {
    // return <FaIcon icon="plus-circle" />
    return(
        <Link href='dadosfuncionarios'>
            <FaIcon icon="plus-circle" />
        </Link>
    )
}

export default class ExibirInicio extends React.Component{
    handleNomeFuncionario = (e) => {
        this.setState({txtNomeFuncionario: e.target.value});
    }
    
    render(){
        return(
            <div>
                <table>
                    <tr>
                        <td width="150"><label>Nome do Funcionário</label></td>
                        <td width="315">
                            <input 
                                type="text" 
                                name="txtNomeFuncionario" 
                                onChange={this.handleNomeFuncionario}
                                style={{width:"300px", height:"5px"}}
                            />
                        </td>
                        <td width="40" title="Pesquisar Funcionários"><Search /></td>
                        <td width="130"><label>Escolher Inclusão</label></td>
                        <td width="140">
                            <select id="optInclusao" style={{width:"125px", height:"auto"}}>
                                <option value="-" selected>-</option>
                                <option value="Funcionario">Funcionário</option>
                                <option value="Mapeamento">Mapeamento</option>
                                <option value="Medidas">Medidas</option>
                            </select>
                        </td>
                        <td width="40" title="Incluir"><PlusCircle /></td>
                    </tr>
                </table>
            </div>
        )
    }
}
