import React from "react"
// import Link from 'next/link'

// import { library } from '@fortawesome/fontawesome-svg-core';
// import { faSearch } from "@fortawesome/free-solid-svg-icons";
// import { faPlusCircle } from "@fortawesome/free-solid-svg-icons";
// library.add( faSearch);
// library.add( faPlusCircle);
// import { FontAwesomeIcon as FaIcon } from "@fortawesome/react-fontawesome";

var nomeFuncionario = "";
// var opcaoInclusao = "";

const PesquisarFuncionario = () => {
    nomeFuncionario = document.querySelector("[name='txtNomeFuncionario']").value;
    
    if(nomeFuncionario == ""){
        alert("Nome deve ser preenchido");
    }
    else{
        alert(nomeFuncionario);
    }
}

// const PlusCircle = () => {
//     return <FaIcon onClick={onClick_PlusCircle} icon="plus-circle" />

//     return(
//         <Link href='dadosfuncionarios'>
//             <FaIcon icon="plus-circle" />
//         </Link>
//     )
// }

// const onClick_PlusCircle = function(){ 
//     opcaoInclusao = document.querySelector("[id='optInclusao']").value;

//     alert(opcaoInclusao);
// }

export default class ExibirConsulta extends React.Component{
    handleNomeFuncionario = (e) => {
        this.setState({txtNomeFuncionario: e.target.value});
    }
    
    render(){
        return(
            <div>
                <a style={{fontSize:"30px", fontWeight:"bold"}} >CONSULTA DE DADOS</a>
                <p/>
                <table>
                    <tr>
                        <td width="150"><label>Nome do Funcionário</label></td>
                        <td width="315">
                            <input 
                                type="text" 
                                name="txtNomeFuncionario" 
                                onChange={this.handleNomeFuncionario}
                                style={{width:"300px", height:"20px"}}
                            />
                        </td>
                        <td width="200">
                            <button 
                                type="button" 
                                onClick={PesquisarFuncionario}
                                style={{width:"185px", height:"28px"}}
                            >
                                Pesquisar Funcionários
                            </button>
                        </td>
                    </tr>
                </table>
            </div>
        )
    }
}
