import React from "react"
import Link from 'next/link'

import { library } from '@fortawesome/fontawesome-svg-core';
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import { faPlusCircle } from "@fortawesome/free-solid-svg-icons";
library.add( faSearch);
library.add( faPlusCircle);
import { FontAwesomeIcon as FaIcon } from "@fortawesome/react-fontawesome";

var nomeFuncionario = "";

const onClick_Search = function(){ 
    nomeFuncionario = document.querySelector("[name='txtNomeFuncionario']").value;
    
    if(nomeFuncionario == ""){
        alert("Nome deve ser preenchido");
    }
    else{
        alert(nomeFuncionario);
    }
}

const Search = () => {
    return <FaIcon onClick={onClick_Search} icon="search" />
};

const PlusCircle = () => {
    // return <FaIcon icon="plus-circle" />
    return(
        <Link href='dadosfuncionarios'>
            <FaIcon icon="plus-circle" />
        </Link>
    )
}

export default class ExibirInicio extends React.Component{
    handleNomeFuncionario = (e) => {
        this.setState({txtNomeFuncionario: e.target.value});
    }
    
    render(){
        return(
            <div>
                <table>
                    <tr>
                        <td width="150"><label>Nome do Funcion치rio</label></td>
                        <TextField
                            id="txtNomeFuncionario"
                            label="Nome do Funcion치rio"
                            style={{ margin: 8, width:"300px" }}
                            margin="normal"
                            InputLabelProps={{
                                shrink: true,
                            }}
                            variant="outlined"
                        />
                        <td width="200">
                            <input type="text" name="txtNomeFuncionario" onChange={this.handleNomeFuncionario}/>
                        </td>
                        <td width="40" title="Pesquisar Funcion치rios"><Search /></td>
                        <td width="40" title="Inserir Novo Funcion치rio"><PlusCircle /></td>
                    </tr>
                </table>
            </div>
        )
    }
}
