                    <TextField
                        id="txtResponsavel"
                        label="Responsável"
                        style={{ margin: 8, width:"300px", marginRight:"10px"}}
                        margin="normal"
                        InputLabelProps={{
                            shrink: true,
                        }}
                        variant="outlined"
                        size="small"
                    />
                    <TextField
                        id="txtStatus"
                        label="Status"
                        style={{ margin: 8, width:"300px", marginRight:"10px"}}
                        margin="normal"
                        InputLabelProps={{
                            shrink: true,
                        }}
                        variant="outlined"
                        size="small"
                    />
                </FormGroup>
                <FormGroup row style={{marginBottom:"20px",marginLeft:"10px",marginRight:"10px"}}>
                    <TextField
                        id="txtDescricaoMedidas"
                        label="Descrição das Medidas"
                        multiline
                        rows={4}
                        fullWidth
                        margin="normal"
                        InputLabelProps={{
                            shrink: true,
                        }}
                        variant="outlined"
                        size="small"
                    />
                </FormGroup>
                <FormGroup row style={{marginBottom:"20px",marginLeft:"10px",marginRight:"10px"}}>
                    <TextField
                        id="txtObsResultados"
                        label="Observação dos Resultados"
                        multiline
