import React from "react"
import styles from '../styles.module.css'

import { library } from '@fortawesome/fontawesome-svg-core';
import { faSearch } from "@fortawesome/free-solid-svg-icons";
library.add( faSearch);
import { FontAwesomeIcon as FaIcon } from "@fortawesome/react-fontawesome";

var nomeFuncionario = "";

const onClick_Search = function(){ 
    if(nomeFuncionario == ""){
        alert("Nome deve ser preenchido");
    }
    else{
        alert(nomeFuncionario);
    }
}

const Search = () => {
    nomeFuncionario = document.querySelector("[name='txtNomeFuncionario']").value;

    return <FaIcon onClick={onClick_Search} icon="search" />
};

export default class ExibirInicio extends React.Component{
    handleNomeFuncionario = (e) => {
        this.setState({txtNomeFuncionario: e.target.value});
    }
    
    render(){
        return(
            <div>
                <table>
                    <tr>
                        <td width="100"><label>Nome do Funcion√°rio</label></td>
                        <td><input type="text" name="txtNomeFuncionario" onChange={this.handleNomeFuncionario} /></td>
                        <td><Search /></td>
                    </tr>
                </table>
            </div>
        )
    }
}
