import React from 'react';
import ViaCep from 'react-via-cep'

export default class InserirNovoFuncionario extends React.Component{
    constructor(props) {
        super(props);
        this.state = { cep: '' };
    
        this.handleChangeCep = this.handleChangeCep.bind(this);
        this.handleSuccess = this.handleSuccess.bind(this);
    }
    
    handleChangeCep(evt) {
        this.setState({ cep: evt.target.value })
    }
    handleSuccess(cepData) {
        console.log(cepData);
    }
    
    render(){
        //const { cep, data, setCep } = useViaCep()
        
        return(
            <div>
                <a style={{fontSize:"30px", fontWeight:"bold"}} >ADICIONAR NOVO FUNCIONÁRIO</a>
                <p/>
                <table>
                    <tr style={{height:"40px"}}>
                        <td width="150"><label>Nome do Funcionário</label></td>
                        <td width="315">
                            <input type="text" name="txtNomeFuncionario" style={{width:"300px", height:"20px"}} />
                        </td>
                        <td width="75"><label>Prontuário</label></td>
                        <td width="200">
                            <input type="text" name="txtProntuario" style={{width:"185px", height:"20px"}} />
                        </td>
                        <td width="140"><label>Data de Nascimento</label></td>
                        <td width="140">
                            <input type="date" name="txtDtNascimento" style={{width:"125px", height:"20px"}} />
                        </td>
                        <td width="40"><label>Sexo</label></td>
                        <td width="65">
                            <select name="optSexo" style={{width:"50px", height:"20px"}}>
                                <option value="-" selected>-</option>
                                <option value="Feminino">F</option>
                                <option value="Masculino">M</option>
                            </select>
                        </td>
                        <td width="90"><label>Estado Civil</label></td>
                        <td width="115">
                            <select name="optEstadoCivil" style={{width:"100px", height:"20px"}}>
                                <option value="-" selected>-</option>
                                <option value="Solteiro">Solteiro</option>
                                <option value="Casado">Casado</option>
                                <option value="Viúvo">Viúvo</option>
                                <option value="Divorciado">Divorciado</option>
                            </select>
                        </td>
                    </tr>
                </table>
                <table>
                    <tr style={{height:"40px"}}>
                        <td width="40"><label>CEP</label></td>
                        <td width="90">
                            {/* <input 
                                type="text" 
                                name="txtCEP" 
                                style={{width:"75px", height:"20px"}} 
                            /> */}
                            <ViaCep cep={this.state.cep} onSuccess={this.handleSuccess} lazy>
                                { ({ data, loading, error, fetch }) => {
                                    if (loading) {
                                        return <p>loading...</p>
                                    }
                                    if (error) {
                                        return <p>error</p>
                                    }
                                    if (data) {
                                        return <div>
                                            <p>
                                                CEP: {data.cep} <br/>
                                                LOGRADOURO: {data.logradouro} <br/>
                                                CIDADE: {data.localidade} <br/>
                                                UF: {data.uf} <br/>
                                            </p>
                                        </div>
                                    }
                                    return <div>
                                        <input 
                                            onChange={this.handleChangeCep} 
                                            value={this.state.cep} 
                                            placeholder="CEP" 
                                            type="text"
                                        />
                                        <button onClick={fetch}>Pesquisar</button>
                                    </div>
                                }}
                            </ViaCep>
                        </td>
                    </tr>
                </table>
            </div>
        )
    }
}
