import React from "react"

import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import SearchIcon from '@material-ui/icons/Search';

var nomeFuncionario = "";

const PesquisarFuncionario = () => {
    nomeFuncionario = document.querySelector("[name='txtNomeFuncionario']").value;
    
    if(nomeFuncionario == ""){
        alert("Nome deve ser preenchido");
    }
    else{
        alert(nomeFuncionario);
    }
}

export default class ExibirConsulta extends React.Component{
    handleNomeFuncionario = (e) => {
        this.setState({txtNomeFuncionario: e.target.value});
    }
    
    render(){
        return(
            <div>
                <a style={{fontSize:"30px", fontWeight:"bold"}} >CONSULTA DE DADOS</a>
                <p/>
                <table>
                    <tr>
                        <td width="150"><label>Nome do Funcionário</label></td>
                        <td width="315">
                            <input 
                                type="text" 
                                name="txtNomeFuncionario" 
                                onChange={this.handleNomeFuncionario}
                                style={{width:"300px", height:"20px"}}
                            />
                        </td>
                        <td width="200">
                            <Button
                                variant="contained"
                                color="primary"
                                size="small"
                                startIcon={<SearchIcon />}
                                onClick={PesquisarFuncionario}
                            >
                                Pesquisar
                            </Button>
                            {/* <button 
                                type="button" 
                                onClick={PesquisarFuncionario}
                                style={{width:"185px", height:"28px"}}
                            >
                                Pesquisar Funcionários
                            </button> */}
                        </td>
                    </tr>
                </table>
            </div>
        )
    }
}
