import React from 'react';
import Select from 'react-select';

import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import SaveIcon from '@material-ui/icons/Save';
import BackIcon from '@material-ui/icons/ArrowBack';

import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';
import { render } from 'react-dom';

const listaSexo = [
    {value: '-', label: '-'},
    {value: 'Feminino', label: 'F'},
    {value: 'Masculino', label: 'M'}
]

const listaEstadoCivil = [
    {value: '-', label: '-'},
    {value: 'Solteiro', label: 'Solteiro'},
    {value: 'Casado', label: 'Casado'},
    {value: 'Viúvo', label: 'Viúvo'},
    {value: 'Divorciado', label: 'Divorciado'}
]

const listaEstado = [
    {value: '-', label: '-'},
    {value: 'Acre', label: 'AC'},
    {value: 'Alagoas ', label: 'AL'},
    {value: 'Amapá ', label: 'AP'},
    {value: 'Amazonas ', label: 'AM'},
    {value: 'Bahia ', label: 'BA'},
    {value: 'Ceará ', label: 'CE'},
    {value: 'Distrito Federal ', label: 'DF'},
    {value: 'Espírito Santo ', label: 'ES'},
    {value: 'Goiás ', label: 'GO'},
    {value: 'Maranhão ', label: 'MA'},
    {value: 'Mato Grosso ', label: 'MT'},
    {value: 'Mato Grosso do Sul ', label: 'MS'},
    {value: 'Minas Gerais ', label: 'MG'},
    {value: 'Pará ', label: 'PA'},
    {value: 'Paraíba ', label: 'PB'},
    {value: 'Paraná ', label: 'PR'},
    {value: 'Pernambuco ', label: 'PE'},
    {value: 'Piauí ', label: 'PI'},
    {value: 'Rio de Janeiro ', label: 'RJ'},
    {value: 'Rio Grande do Norte ', label: 'RN'},
    {value: 'Rio Grande do Sul ', label: 'RS'},
    {value: 'Rondônia ', label: 'RO'},
    {value: 'Roraima ', label: 'RR'},
    {value: 'Santa Catarina ', label: 'SC'},
    {value: 'São Paulo ', label: 'SP'},
    {value: 'Sergipe ', label: 'SE'},
    {value: 'Tocantins ', label: 'TO'}
]

const useStyles = makeStyles((theme) => ({
    button: {
      margin: theme.spacing(1),
    },
    root: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    margin: {
        margin: theme.spacing(1),
    },
    withoutLabel: {
        marginTop: theme.spacing(3),
    },
    textField: {
        width: '25ch',
    },
}));

// var nomeFuncionario = "";
var itemSexo = "";

const SalvarDados = () => {
    itemSexo = document.querySelector("[id='txtSexo']").value;
    alert(itemSexo);
    // Acessar API de salvar dados no Banco
    alert("Os dados foram salvos com sucesso!");
}

export default function InserirNovoFuncionario() {
    const [sexo, setSexo] = React.useState('-');
    const [estadoCivil, setEstadoCivil] = React.useState('-');
    const [estado, setEstado] = React.useState('-');
    
    const handleChangeSexo = event => {
        document.querySelector("[id='txtSexo']").value = event.target.value;
        setSexo(event.target.value);
    };

    const handleChangeEstadoCivil = event => {
        document.querySelector("[id='txtEstadoCivil']").value = event.target.value;
        setEstadoCivil(event.target.value);
    };

    const handleChangeEstado = event => {
        document.querySelector("[id='txtEstado']").value = event.target.value;
        setEstado(event.target.value);
    };

    return(
        <div>
            <a style={{fontSize:"30px", fontWeight:"bold"}} >ADICIONAR NOVO FUNCIONÁRIO</a>
            <p/>
            <Button
                variant="contained"
                color="primary"
                size="large"
                startIcon={<SaveIcon />}
                onClick={SalvarDados}
                style={{marginRight:"20px"}}
            >
                Salvar
            </Button>
            <Button
                variant="contained"
                color="primary"
                size="large"
                startIcon={<BackIcon />}
                href="principal"
            >
                Voltar
            </Button>
            <br />
            <br />
            <br />
            <TextField
                id="txtNomeFuncionario"
                label="Nome do Funcionário"
                style={{ margin: 8, width:"300px", marginRight:"10px"}}
                margin="normal"
                InputLabelProps={{
                    shrink: true,
                }}
                variant="outlined"
                size="small"
            />
            <TextField
                id="txtDtNascimento"
                label="Data de Nascimento"
                style={{ margin: 8, width:"180px", marginRight:"10px"}}
                type="date"
                format="dd/MM/yyyy"
                margin="normal"
                InputLabelProps={{
                    shrink: true,
                }}
                variant="outlined"
                size="small"
            />
            <TextField
                id="txtSexo"
                select
                label="Sexo"
                style={{ margin: 8, width:"100px", marginRight:"10px"}}
                size="small"
                value={sexo}
                onChange={handleChangeSexo}
                variant="outlined"
            >
                {listaSexo.map((option) => (
                    <MenuItem key={option.value} value={option.value}>
                        {option.label}
                    </MenuItem>
                ))}
            </TextField>
            <TextField
                id="txtEstadoCivil"
                select
                label="Estado Civil"
                style={{ margin: 8, width:"150px", marginRight:"10px"}}
                size="small"
                value={estadoCivil}
                onChange={handleChangeEstadoCivil}
                variant="outlined"
            >
                {listaEstadoCivil.map((option) => (
                    <MenuItem key={option.value} value={option.value}>
                        {option.label}
                    </MenuItem>
                ))}
            </TextField>
            <p />
            <TextField
                id="txtArea"
                label="Área"
                style={{ margin: 8, width:"50px", marginRight:"10px"}}
                margin="normal"
                InputLabelProps={{
                    shrink: true,
                }}
                variant="outlined"
                size="small"
            />
            <TextField
                id="txtTelFuncionario"
                label="Telefone"
                style={{ margin: 8, width:"120px", marginRight:"10px"}}
                margin="normal"
                InputLabelProps={{
                    shrink: true,
                }}
                variant="outlined"
                size="small"
            />
            <TextField
                id="txtCargo"
                label="Cargo"
                style={{ margin: 8, width:"130px", marginRight:"10px"}}
                margin="normal"
                InputLabelProps={{
                    shrink: true,
                }}
                variant="outlined"
                size="small"
            />
            <TextField
                id="txtGestor"
                label="Gestor"
                style={{ margin: 8, width:"300px", marginRight:"10px"}}
                margin="normal"
                InputLabelProps={{
                    shrink: true,
                }}
                variant="outlined"
                size="small"
            />
            <TextField
                id="txtTelGestor"
                label="Telefone Gestor"
                style={{ margin: 8, width:"160px", marginRight:"10px"}}
                margin="normal"
                InputLabelProps={{
                    shrink: true,
                }}
                variant="outlined"
                size="small"
            />
            <p />
            <TextField
                id="txtCEP"
                label="CEP"
                style={{ margin: 8, width:"75px", marginRight:"10px"}}
                margin="normal"
                InputLabelProps={{
                    shrink: true,
                }}
                variant="outlined"
                size="small"
            />
            <TextField
                id="txtEndereco"
                label="Endereço"
                style={{ margin: 8, width:"300px", marginRight:"10px"}}
                margin="normal"
                InputLabelProps={{
                    shrink: true,
                }}
                variant="outlined"
                size="small"
            />
            <TextField
                id="txtBairro"
                label="Bairro"
                style={{ margin: 8, width:"200px", marginRight:"10px"}}
                margin="normal"
                InputLabelProps={{
                    shrink: true,
                }}
                variant="outlined"
                size="small"
            />
            <TextField
                id="txtCidade"
                label="Cidade"
                style={{ margin: 8, width:"200px", marginRight:"10px"}}
                margin="normal"
                InputLabelProps={{
